mapper.select:
select prj_item_id as prjItemId,prj_item_name as prjItemName,prj_item_num as prjItemNum,prj_item_type as prjItemType,
    prj_name as prjName,company_name as companyName,belong_company as belongCompany,person_in_charge as personInCharge,
    person_tel as personTel,progress_of_item as progressOfItem,entry_time as entryTime,finish_time as finishTime,prj_item_place as prjItemPlace
from tx_project_item t
left join tx_company_info c on t.belong_company = c.company_Id


        and  prj_item_name like #{ProjectItemInfo.prjItemName}


        and  prj_name  like #{ProjectItemInfo.prjName}


        and  prj_item_type = #{ProjectItemInfo.prjItemType}


        and  companyName like #{ProjectItemInfo.belongCompany}


        and  prj_item_num like #{ProjectItemInfo.prjItemNum}


        and  person_in_charge like #{ProjectItemInfo.personInCharge}


        and  progress_of_item like #{ProjectItemInfo.progressOfItem}


        and  entry_time = #{ProjectItemInfo.entryTime}


        and  finish_time = #{ProjectItemInfo.finishTime}



order by t.create_time desc
