mapper.select:
select t.prj_item_id as prjItemId, prj_item_name as prjItemName,prj_item_num as prjItemNum,prj_item_type as prjItemType,d.dict_code_name as itemTypeName, prj_name as prjName,company_name as companyName,belong_company as belongCompany,person_in_charge as personInCharge,t.remark, person_tel as personTel,progress_of_item as progressOfItem,entry_time as entryTime,finish_time as finishTime,i.contract_id as contractId, i.contract_name as contractName,require_deploy_time as requireDeployTime,prj_item_place as prjItemPlace,prj_item_status as prjItemStatus , ifnull(t.file_rel_id,'') as fileRelId i.contract_name as contractName,require_deploy_time as requireDeployTime,prj_item_place as prjItemPlace,prj_item_status as prjItemStatus, t.file_rel_id as fileRelId,length(t.file_rel_id)-length(replace(t.file_rel_id,',','')) as fileCount from tx_project_item t left join tx_company_info c on t.belong_company = c.company_Id left join tx_pro_contract_rel r on r.prj_item_id = t.prj_item_id left join tx_contract_info i on i.contract_id = r.contract_id left join tx_dict d on t.prj_item_type = d.dict_code_id and d.dict_code='PROJECTITEMTYPE'
and prj_item_name like CONCAT('%',#{ProjectItemInfo.prjItemName},'%')
and prj_name like CONCAT('%',#{ProjectItemInfo.prjName},'%')
and prj_item_type = #{ProjectItemInfo.prjItemType}
and c.company_name like CONCAT('%',#{ProjectItemInfo.belongCompany},'%')
and prj_item_num like CONCAT('%',#{ProjectItemInfo.prjItemNum},'%')
and person_in_charge like CONCAT('%',#{ProjectItemInfo.personInCharge},'%')
and progress_of_item like CONCAT('%',#{ProjectItemInfo.progressOfItem},'%')
and prj_item_status = #{ProjectItemInfo.prjItemStatus}
and entry_time = #{ProjectItemInfo.entryTime}
and finish_time = #{ProjectItemInfo.finishTime}
and require_deploy_time = #{ProjectItemInfo.requireDeployTime}
and ifnull(has_connection,'0') = #{ProjectItemInfo.hasConnection}
order by t.create_time desc

select o.out_id, info.equip_name,info.equip_model,info.equip_no,info.use_times from tx_out_equip o
LEFT JOIN tx_equip_info info ON o.equip_id = info.equip_id
where o.prj_item_id = #{projectItemId}
